const navMap={home:"index.html",mall:"mall.html",code:"code.html",voucher:"voucher.html",profile:"profile.html"};function loadPage(t){if(navMap[t]){if("home"===t)return;fetch(navMap[t]).then(t=>t.text()).then(t=>{const e=(new DOMParser).parseFromString(t,"text/html").querySelector(".content");e&&(document.getElementById("main-content").innerHTML=e.innerHTML)})}}function highlightNav(){var t=document.querySelectorAll(".bottom-nav .nav-item");t.forEach(function(t){t.classList.remove("active")});var e=location.pathname;e.endsWith("index.html")||"/"===e||"/index"===e?t[0].classList.add("active"):e.endsWith("mall.html")?t[1].classList.add("active"):e.endsWith("code.html")?t[2].classList.add("active"):e.endsWith("voucher.html")?t[3].classList.add("active"):e.endsWith("profile.html")&&t[4].classList.add("active")}window.addEventListener("DOMContentLoaded",highlightNav),document.querySelectorAll(".bottom-nav .nav-item").forEach(t=>{t.addEventListener("click",function(){setTimeout(highlightNav,10);loadPage(this.getAttribute("data-page"))})});