document.addEventListener("DOMContentLoaded",function(){var t=document.querySelectorAll(".tabs__item"),e=document.querySelectorAll(".tab-content");t.forEach(function(c,n){c.addEventListener("click",function(){t.forEach(function(t){t.classList.remove("tabs__item--active")}),e.forEach(function(t){t.classList.remove("tab-content--active")}),c.classList.add("tabs__item--active"),e[n].classList.add("tab-content--active")})})});